void Enemy_Move(int i)
{
	enemy[i].z += enemy[i].dz;
	if (enemy[i].z < 1)
	{
		enemy[i].dz *= -1;
		enemy[i].z = 1.1;
	}
	enemy[i].dz -= enemy[i].gravity;
}

