struct {
	float x, y, z;
	float Xrot, Zrot;
}camera = {10,5,2.5,30,-45};

void Camera_Apply()
{
	glRotatef(-camera.Xrot, 1, 0, 0);
	glRotatef(-camera.Zrot, 0, 0, 1);
	glTranslatef(-camera.x, -camera.y, -camera.z);
}

void Camera_Rotation(float xAngle, float zAngle)
{
	camera.Zrot += zAngle;
	if (camera.Zrot < 0) camera.Zrot += 360;
	if (camera.Zrot > 360) camera.Zrot -= 360;
	camera.Xrot += xAngle;
	if (camera.Xrot < 0) camera.Xrot = 0;
	if (camera.Xrot > 360) camera.Xrot = 180;
}

void Player_Move()
{
	if (GetForegroundWindow() != hwnd) return; //       

	float ugol = -camera.Zrot / 180 * M_PI;
	float speed = 0;
	if ((GetKeyState('W') < 0) && (GetKeyState('A') < 0)) ugol += M_PI * 0.25;
	if ((GetKeyState('W') < 0) && (GetKeyState('D') < 0)) ugol -= M_PI * 0.25;
	if ((GetKeyState('S') < 0) && (GetKeyState('A') < 0)) ugol -= M_PI * 0.25;
	if ((GetKeyState('S') < 0) && (GetKeyState('D') < 0)) ugol += M_PI * 0.25;
	if (GetKeyState('W') < 0) speed = 0.1;
	if (GetKeyState('S') < 0) speed = -0.1;
	if (GetKeyState('A') < 0) { speed = 0.1; ugol -= M_PI * 0.5; }
	if (GetKeyState('D') < 0) { speed = 0.1; ugol += M_PI * 0.5; }
	if (speed != 0)
	{
		float sinu = sin(ugol) * speed;
		float cosu = cos(ugol) * speed;
		if ((camera.x + sinu < pW - 0.5) && (camera.x + sinu > 0.5))
			camera.x += sinu;
		if ((camera.y + cosu < 10) && (camera.y + cosu > 0.5))
			camera.y += cosu;
	}

	POINT cur;
	static POINT base = { 400,300 };
	GetCursorPos(&cur); //   
	Camera_Rotation((base.y - cur.y) / 5.0, (base.x - cur.x) / 5.0);//      base      
	SetCursorPos(base.x, base.y); //      base
}

